'use client'

import React, { useState } from 'react'
import { useRouter } from 'next/router'
import { ChevronDown } from 'lucide-react'
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Card, CardContent } from "@/components/ui/card"
import Image from 'next/image'

export default function LoginPage() {
  const [userType, setUserType] = useState('employee')
  const [userId, setUserId] = useState('')
  const [password, setPassword] = useState('')
  const [isCreatingPassword, setIsCreatingPassword] = useState(false)
  const [error, setError] = useState('')
  const router = useRouter()

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault()
    setError('')
    console.log('Attempting login with:', { userType, userId, password })
    if (userType === 'employee') {
      try {
        // Simulating API call
        const response = await new Promise<{ requiresPasswordCreation: boolean }>((resolve) => 
          setTimeout(() => resolve({ requiresPasswordCreation: false }), 1000)
        )
        if (response.requiresPasswordCreation) {
          setIsCreatingPassword(true)
        } else {
          localStorage.setItem('isAuthenticated', 'true')
          localStorage.setItem('userType', 'employee')
          localStorage.setItem('userId', userId)
          router.push('/employee-home')
        }
      } catch (error) {
        console.error('Login error:', error)
        setError('An error occurred during login. Please try again.')
      }
    } else if (userType === 'admin' && userId === 'admin1' && password === 'admin123') {
      localStorage.setItem('isAuthenticated', 'true')
      localStorage.setItem('userType', 'admin')
      router.push('/home')
    } else {
      setError('Invalid credentials')
    }
  }

  const handleCreatePassword = async (e: React.FormEvent) => {
    e.preventDefault()
    setError('')
    try {
      // Simulating API call
      await new Promise((resolve) => setTimeout(resolve, 1000))
      alert('Password created successfully. Please log in.')
      setIsCreatingPassword(false)
    } catch (error) {
      setError('An error occurred while creating the password. Please try again.')
    }
  }

  return (
    <div className="flex min-h-screen bg-gray-900">
      <div className="w-1/2 flex flex-col justify-center px-16">
        <h1 className="text-6xl font-bold text-white mb-4">KAPSRI</h1>
        <h2 className="text-6xl font-bold text-white mb-8">TECHNOLOGIES</h2>
        <p className="text-xl text-gray-400 mb-8">Your one-stop solution for all your Fire and safety Needs.</p>
        <div className="flex space-x-4">
          <Image src="/placeholder.svg?height=50&width=50" alt="HDFC Bank" width={50} height={50} className="object-contain" />
          <Image src="/placeholder.svg?height=50&width=50" alt="Zomato" width={50} height={50} className="object-contain" />
          <Image src="/placeholder.svg?height=50&width=50" alt="Swiggy" width={50} height={50} className="object-contain" />
          <Image src="/placeholder.svg?height=50&width=50" alt="Fireside" width={50} height={50} className="object-contain" />
          <span className="text-white self-center">+25 more</span>
        </div>
      </div>
      <div className="w-1/2 bg-white flex items-center justify-center">
        <Card className="w-96">
          <CardContent className="pt-6">
            <Image src="/placeholder.svg?height=100&width=100" alt="Logo" width={100} height={100} className="mx-auto mb-8" />
            <form onSubmit={isCreatingPassword ? handleCreatePassword : handleLogin} className="space-y-4">
              {!isCreatingPassword && (
                <Select value={userType} onValueChange={setUserType}>
                  <SelectTrigger className="w-full">
                    <SelectValue placeholder="Select user type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="employee">Employee</SelectItem>
                    <SelectItem value="admin">Admin</SelectItem>
                  </SelectContent>
                </Select>
              )}
              <Input
                type="text"
                value={userId}
                onChange={(e) => setUserId(e.target.value)}
                placeholder={userType === 'employee' ? 'User ID' : 'Username'}
                required
              />
              <Input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Password"
                required
              />
              <Button type="submit" className="w-full">
                {isCreatingPassword ? 'Create Password' : 'Login'}
              </Button>
            </form>
            {error && <p className="mt-4 text-sm text-red-600 text-center">{error}</p>}
            <Button variant="link" className="w-full mt-4">
              Forgot Password?
            </Button>
          </CardContent>
        </Card>
      </div>
    </div>
  )
}
