import React, { useState, useEffect, useMemo } from 'react'
import { Input } from "../components/ui/input"
import { Button } from "../components/ui/button"
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "../components/ui/Table"
import { Card, CardContent, CardHeader, CardTitle } from "../components/ui/card"
import { BarChart, Bar, PieChart, Pie, Cell, ResponsiveContainer, Tooltip, XAxis, YAxis } from 'recharts'
import { Search, Filter } from 'lucide-react'

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

const Accounts = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [transactions, setTransactions] = useState([]);
  const [monthlyData, setMonthlyData] = useState([]);
  const [expensesBreakdown, setExpensesBreakdown] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      setError(null);
      try {
        await Promise.all([
          fetchTransactions(),
          fetchMonthlyData(),
          fetchExpensesBreakdown()
        ]);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Failed to fetch data. Please try again later.');
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const fetchTransactions = async () => {
    try {
      const response = await fetch('http://localhost:5038/transactions');
      if (!response.ok) throw new Error('Failed to fetch transactions');
      const data = await response.json();
      setTransactions(data);
    } catch (error) {
      console.error('Error fetching transactions:', error);
    }
  };

  const fetchMonthlyData = async () => {
    try {
      const response = await fetch('http://localhost:5038/transactions/monthly');
      if (!response.ok) throw new Error('Failed to fetch monthly data');
      const data = await response.json();
      console.log('Monthly data received:', data);
      setMonthlyData(data);
    } catch (error) {
      console.error('Error fetching monthly data:', error);
    }
  };

  const fetchExpensesBreakdown = async () => {
    try {
      const response = await fetch('http://localhost:5038/transactions/expenses');
      if (!response.ok) throw new Error('Failed to fetch expenses breakdown');
      const data = await response.json();
      setExpensesBreakdown(data);
    } catch (error) {
      console.error('Error fetching expenses breakdown:', error);
    }
  };

  const filteredTransactions = useMemo(() => {
    return transactions.filter(transaction =>
      Object.values(transaction).some(value =>
        value !== null && value !== undefined && value.toString().toLowerCase().includes(searchTerm.toLowerCase())
      )
    );
  }, [searchTerm, transactions]);

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  const calculateTotalRevenue = () => {
    console.log('Calculating total revenue');
    console.log('Transactions:', transactions);
    const total = transactions
      .filter(t => t.type.toLowerCase() === 'received' || t.type.toLowerCase() === 'recieved')
      .reduce((sum, t) => {
        console.log(`Adding transaction: ${t.amount}`);
        return sum + (t.amount || 0)
      }, 0);
    console.log('Total revenue:', total);
    return total;
  };

  const calculateTotalSpend = () => {
    console.log('Calculating total spend');
    const total = transactions
      .filter(t => t.type.toLowerCase() === 'sent')
      .reduce((sum, t) => sum + (t.amount || 0), 0);
    console.log('Total spend:', total);
    return total;
  };

  const calculateAccountBalance = () => {
    const balance = calculateTotalRevenue() - calculateTotalSpend();
    console.log('Account balance:', balance);
    return balance;
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', { style: 'currency', currency: 'INR' }).format(amount);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', { 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric' 
    });
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-4xl font-bold mb-6">Accounts</h1>

      <h2 className="text-2xl font-semibold mb-4">Dashboard</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
        <Card>
          {/* <CardHeader>
            <CardTitle>Revenue every month</CardTitle>
          </CardHeader> */}
          <CardContent className="flex flex-col justify-between gap-4">
          <div className='flex flex-col items-center gap-2'>
            <div className="text-2xl font-bold mt-2">{formatCurrency(calculateTotalRevenue())}</div>
            <div className="text-sm text-gray-500">Total Revenue</div>
            </div>
            <ResponsiveContainer width="100%" height={200}>
              <BarChart data={monthlyData}>
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip formatter={(value) => formatCurrency(value)} />
                <Bar dataKey="received" fill="#8884d8" radius={[15, 15, 0, 0]} /> {/* Add radius here */}
              </BarChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>

        <Card>
          {/* <CardHeader>
            <CardTitle>Spend every month</CardTitle>
          </CardHeader> */}
          <CardContent className="flex flex-col justify-between gap-4">
            <div className='flex flex-col items-center gap-2'>
            <div className="text-2xl font-bold mt-2">{formatCurrency(calculateTotalSpend())}</div>
            <div className="text-sm text-gray-500">Total Spend</div>
            </div>
            <ResponsiveContainer width="100%" height={200}>
              <BarChart data={monthlyData}>
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip formatter={(value) => formatCurrency(value)} />
                <Bar dataKey="sent" fill="#ffc658" radius={[15, 15, 0, 0]} /> {/* Add radius here */}
              </BarChart>
            </ResponsiveContainer>

          </CardContent>
        </Card>

        <Card>
          {/* <CardHeader>
            <CardTitle>Expenses breakdown</CardTitle>
          </CardHeader> */}
          <CardContent>
            <div className='flex flex-col items-center gap-2'>  
            <div className="text-2xl font-bold mt-2">{formatCurrency(calculateTotalSpend())}</div>
            <div className="text-sm text-gray-500">Total Expenses</div>
            </div>
            <ResponsiveContainer width="100%" height={200}>
              <PieChart>
                <Pie
                  data={expensesBreakdown}
                  cx="50%"
                  cy="50%"
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                  label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                >
                  {expensesBreakdown.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip formatter={(value) => formatCurrency(value)} />
              </PieChart>
            </ResponsiveContainer>

          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Account Balance</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-4xl font-bold">{formatCurrency(calculateAccountBalance())}</div>
            <div className="text-green-500 text-lg">
              {((calculateAccountBalance() / calculateTotalRevenue()) * 100).toFixed(2)}% ↑
            </div>
            <div className="text-sm text-gray-500">Current Balance</div>
          </CardContent>
        </Card>
      </div>

      <h2 className="text-2xl font-semibold mb-4">Transactions</h2>
      <div className="flex justify-between items-center mb-4">
        <div className="relative flex-grow mr-4">
          <Search className="absolute left-2 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
          <Input
            className="pl-8"
            placeholder="Search for transaction"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <Button variant="outline">
          <Filter size={20} className="mr-2" /> Filter
        </Button>
      </div>

      <div className="overflow-x-auto">
        <Table className="w-full">
          <TableHeader>
            <TableRow className="bg-gray-50">
              <TableHead className="font-semibold text-gray-600">type</TableHead>
              <TableHead className="font-semibold text-gray-600">category</TableHead>
              <TableHead className="font-semibold text-gray-600">subcategory</TableHead>
              <TableHead className="font-semibold text-gray-600">description</TableHead>
              <TableHead className="font-semibold text-gray-600">recipient</TableHead>
              <TableHead className="font-semibold text-gray-600">amount</TableHead>
              <TableHead className="font-semibold text-gray-600">date</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {filteredTransactions.map((transaction, index) => (
              <TableRow 
                key={transaction.id}
                className={`
                  ${index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}
                  }
                `}
                style={{
                  border: '1px solid #e0e0e0',
                }}
              >
                <TableCell className="py-4">{transaction.type}</TableCell>
                <TableCell>{transaction.category}</TableCell>
                <TableCell>{transaction.subcategory}</TableCell>
                <TableCell>{transaction.description}</TableCell>
                <TableCell>{transaction.recipient}</TableCell>
                <TableCell>{formatCurrency(transaction.amount)}</TableCell>
                <TableCell>{formatDate(transaction.date)}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>
    </div>
  );
};

export default Accounts;
