import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles/login.css';

const Login = () => {
  const [userType, setUserType] = useState('employee');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    // Here you would typically validate the credentials against a backend
    // For this example, we'll use hardcoded credentials
    if (userType === 'employee' && username === 'emp1' && password === 'pass123') {
      // Store authentication state (you might want to use a more secure method in a real app)
      localStorage.setItem('isAuthenticated', 'true');
      localStorage.setItem('userType', 'employee');
      navigate('/employee-home');
    } else if (userType === 'admin' && username === 'admin1' && password === 'admin123') {
      localStorage.setItem('isAuthenticated', 'true');
      localStorage.setItem('userType', 'admin');
      navigate('/home');
    } else {
      alert('Invalid credentials');
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleLogin} className="login-form">
        <h2>Login</h2>
        <div className="form-group">
          <label htmlFor="userType">User Type:</label>
          <select
            id="userType"
            value={userType}
            onChange={(e) => setUserType(e.target.value)}
          >
            <option value="employee">Employee</option>
            <option value="admin">Admin</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
