import React, { useState, useEffect, useMemo } from 'react'
import { Input } from "../components/ui/input"
import { Button } from "../components/ui/button"
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "../components/ui/Table"
import { Card, CardContent, CardHeader, CardTitle } from "../components/ui/card"
import { BarChart, Bar, PieChart, Pie, Cell, ResponsiveContainer, Tooltip, XAxis, YAxis } from 'recharts'
import { Search, Filter } from 'lucide-react'

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

const Accounts = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [transactions, setTransactions] = useState([]);
  const [monthlyData, setMonthlyData] = useState([]);
  const [expensesBreakdown, setExpensesBreakdown] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      setError(null);
      try {
        await Promise.all([
          fetchTransactions(),
          fetchMonthlyData(),
          fetchExpensesBreakdown()
        ]);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Failed to fetch data. Please try again later.');
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const fetchTransactions = async () => {
    try {
      const response = await fetch('http://localhost:5038/transactions');
      if (!response.ok) throw new Error('Failed to fetch transactions');
      const data = await response.json();
      setTransactions(data);
    } catch (error) {
      console.error('Error fetching transactions:', error);
    }
  };

  const fetchMonthlyData = async () => {
    try {
      const response = await fetch('http://localhost:5038/transactions/monthly');
      if (!response.ok) throw new Error('Failed to fetch monthly data');
      const data = await response.json();
      setMonthlyData(data);
    } catch (error) {
      console.error('Error fetching monthly data:', error);
    }
  };

  const fetchExpensesBreakdown = async () => {
    try {
      const response = await fetch('http://localhost:5038/transactions/expenses');
      if (!response.ok) throw new Error('Failed to fetch expenses breakdown');
      const data = await response.json();
      setExpensesBreakdown(data);
    } catch (error) {
      console.error('Error fetching expenses breakdown:', error);
    }
  };

  const filteredTransactions = useMemo(() => {
    return transactions.filter(transaction =>
      Object.values(transaction).some(value =>
        value !== null && value !== undefined && value.toString().toLowerCase().includes(searchTerm.toLowerCase())
      )
    );
  }, [searchTerm, transactions]);

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  const calculateTotalRevenue = () => {
    return transactions
      .filter(t => t.type === 'received')
      .reduce((sum, t) => sum + (t.amount || 0), 0);
  };

  const calculateTotalSpend = () => {
    return transactions
      .filter(t => t.type === 'sent')
      .reduce((sum, t) => sum + (t.amount || 0), 0);
  };

  const calculateAccountBalance = () => {
    return calculateTotalRevenue() - calculateTotalSpend();
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-4xl font-bold mb-6">Accounts</h1>

      <h2 className="text-2xl font-semibold mb-4">Dashboard</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
        <Card>
          <CardHeader>
            <CardTitle>Revenue every month</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={200}>
              <BarChart data={monthlyData}>
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="received" fill="#8884d8" />
              </BarChart>
            </ResponsiveContainer>
            <div className="text-2xl font-bold mt-2">₹{calculateTotalRevenue().toLocaleString()}</div>
            <div className="text-sm text-gray-500">Revenue every month</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Spend every month</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={200}>
              <BarChart data={monthlyData}>
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="sent" fill="#ffc658" />
              </BarChart>
            </ResponsiveContainer>
            <div className="text-2xl font-bold mt-2">₹{calculateTotalSpend().toLocaleString()}</div>
            <div className="text-sm text-gray-500">Spend every month</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Expenses breakdown</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={200}>
              <PieChart>
                <Pie
                  data={expensesBreakdown}
                  cx="50%"
                  cy="50%"
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                  label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                >
                  {expensesBreakdown.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
            <div className="text-2xl font-bold mt-2">₹{calculateTotalSpend().toLocaleString()}</div>
            <div className="text-sm text-gray-500">Expenses breakdown</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Account Balance</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-4xl font-bold">₹{calculateAccountBalance().toLocaleString()}</div>
            <div className="text-green-500 text-lg">
              {((calculateAccountBalance() / calculateTotalRevenue()) * 100).toFixed(2)}% ↑
            </div>
            <div className="text-sm text-gray-500">Account Balance</div>
          </CardContent>
        </Card>
      </div>

      <h2 className="text-2xl font-semibold mb-4">Transactions</h2>
      <div className="flex justify-between items-center mb-4">
        <div className="relative flex-grow mr-4">
          <Search className="absolute left-2 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
          <Input
            className="pl-8"
            placeholder="Search for transaction"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <Button variant="outline">
          <Filter size={20} className="mr-2" /> Filter
        </Button>
      </div>

      <Table>
        <TableHeader>
          <TableRow>
            <TableHead>Type</TableHead>
            <TableHead>Category</TableHead>
            <TableHead>Subcategory</TableHead>
            <TableHead>Description</TableHead>
            <TableHead>Recipient</TableHead>
            <TableHead>Amount</TableHead>
            <TableHead>Date</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {filteredTransactions.map((transaction) => (
            <TableRow key={transaction._id}>
              <TableCell>{transaction.type}</TableCell>
              <TableCell>{transaction.category}</TableCell>
              <TableCell>{transaction.subcategory}</TableCell>
              <TableCell>{transaction.description}</TableCell>
              <TableCell>{transaction.to}</TableCell>
              <TableCell>₹{transaction.amount ? transaction.amount.toLocaleString() : 'N/A'}</TableCell>
              <TableCell>{transaction.date ? new Date(transaction.date).toLocaleDateString() : 'N/A'}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  );
};

export default Accounts;
