import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Home, FileText, Users, CreditCard, PhoneCall, LogOut, Bell } from "lucide-react";
import { useAuth } from '../context/AuthContext'; // We'll create this context

const Navigation = () => {
  const { userType, logout } = useAuth();
  const navigate = useNavigate();

  const adminNavItems = [
    { icon: Home, label: "Home", path: "/" },
    { icon: FileText, label: "Projects", path: "/projects" },
    { icon: Users, label: "Team", path: "/team" },
    { icon: CreditCard, label: "Accounts", path: "/accounts" },
    { icon: PhoneCall, label: "CRM", path: "/crm" },
    // { icon: Bell, label: "Notifications" },
  ];

  const employeeNavItems = [
    { icon: Home, label: "Home", path: "/employee-home" },
  ];

  const navItems = userType === 'admin' ? adminNavItems : employeeNavItems;

  const showNotifications = () => {
    console.log('Notifications');
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div className="bg-white p-6">
      <nav className="flex justify-between items-center mb-8">
        <div className="w-40 h-12 bg-red-500 rounded-md"></div>
        <div className="flex space-x-6">
          {navItems.map((item, index) => (
            <Link
              key={index}
              to={item.path}
              className="flex items-center text-gray-600 hover:text-gray-900"
            >
              <item.icon className="w-5 h-5 mr-1" />
              <span className="text-sm">{item.label}</span>
            </Link>
          ))}
          <div className='relative'>
          <button onClick={showNotifications}>
            <div>
            <Bell className="w-5 h-5 mr-1" />
            </div>
            {/* <span className="text-sm">Notifications</span> */}
          </button>
          <div className='absolute bottom-100 left-0'>
            <div className='w-40 h-40 bg-grey-500 rounded-md'>
              <ul>
                <li>Notification 1</li>
                <li>Notification 2</li>
                <li>Notification 3</li>
              </ul>
            </div>
          </div>
          </div>
          <button
            onClick={handleLogout}
            className="flex items-center text-gray-600 hover:text-gray-900"
          >
            <LogOut className="w-5 h-5 mr-1" />
            <span className="text-sm">Logout</span>
          </button>
        </div>
        <div className="w-12 h-12 bg-yellow-400 rounded-full"></div>
      </nav>
    </div>
  );
};

export default Navigation;
