import React, { useState, useMemo } from 'react'
import { Input } from "../components/ui/input"
import { Button } from "../components/ui/button"
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "../components/ui/table"
import { Card, CardContent, CardHeader, CardTitle } from "../components/ui/card"
import { BarChart, Bar, PieChart, Pie, Cell, ResponsiveContainer, Tooltip, XAxis, YAxis } from 'recharts'
import { Search, Filter } from 'lucide-react'

// Dummy transaction data
const dummyTransactions = [
  { id: 1, type: "received", category: "service", subcategory: "amc", description: "Annual maintenance contract", to: "ABC Corp", amount: 100000, date: "2024-08-20" },
  { id: 2, type: "received", category: "service", subcategory: "NOC", description: "Network operations center service", to: "XYZ Ltd", amount: 300000, date: "2024-07-20" },
  { id: 3, type: "sent", category: "Purchase", subcategory: "Material Purchase", description: "Fire extinguishers", to: "Safety Supplies Inc", amount: 25000, date: "2024-07-20" },
  { id: 4, type: "received", category: "service", subcategory: "Refilling", description: "Fire extinguisher refilling", to: "Building Management", amount: 700000, date: "2024-08-20" },
  { id: 5, type: "sent", category: "Purchase", subcategory: "Equipment", description: "New fire alarm system", to: "Fire Tech Co", amount: 33000, date: "2024-08-20" },
  { id: 6, type: "received", category: "service", subcategory: "Installation", description: "Fire safety system installation", to: "Skyscraper Inc", amount: 440000, date: "2024-06-04" },
];

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

const Accounts = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const filteredTransactions = useMemo(() => {
    return dummyTransactions.filter(transaction =>
      Object.values(transaction).some(value =>
        value.toString().toLowerCase().includes(searchTerm.toLowerCase())
      )
    );
  }, [searchTerm]);

  const calculateTotalRevenue = () => {
    return dummyTransactions
      .filter(t => t.type === 'received')
      .reduce((sum, t) => sum + t.amount, 0);
  };

  const calculateTotalSpend = () => {
    return dummyTransactions
      .filter(t => t.type === 'sent')
      .reduce((sum, t) => sum + t.amount, 0);
  };

  const calculateAccountBalance = () => {
    return calculateTotalRevenue() - calculateTotalSpend();
  };

  const getMonthlyData = (type) => {
    const monthlyData = Array(12).fill(0);
    dummyTransactions
      .filter(t => t.type === type)
      .forEach(t => {
        const month = new Date(t.date).getMonth();
        monthlyData[month] += t.amount;
      });
    return monthlyData.map((amount, index) => ({
      name: new Date(0, index).toLocaleString('default', { month: 'short' }),
      amount
    }));
  };

  const getExpensesBreakdown = () => {
    const breakdown = {};
    dummyTransactions
      .filter(t => t.type === 'sent')
      .forEach(t => {
        breakdown[t.category] = (breakdown[t.category] || 0) + t.amount;
      });
    return Object.entries(breakdown).map(([name, value]) => ({ name, value }));
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-4xl font-bold mb-6">Accounts</h1>

      <h2 className="text-2xl font-semibold mb-4">Dashboard</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
        <Card>
          <CardHeader>
            <CardTitle>Revenue every month</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={200}>
              <BarChart data={getMonthlyData('received')}>
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="amount" fill="#8884d8" />
              </BarChart>
            </ResponsiveContainer>
            <div className="text-2xl font-bold mt-2">₹{calculateTotalRevenue().toLocaleString()}</div>
            <div className="text-sm text-gray-500">Revenue every month</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Spend every month</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={200}>
              <BarChart data={getMonthlyData('sent')}>
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="amount" fill="#ffc658" />
              </BarChart>
            </ResponsiveContainer>
            <div className="text-2xl font-bold mt-2">₹{calculateTotalSpend().toLocaleString()}</div>
            <div className="text-sm text-gray-500">Spend every month</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Expenses breakdown</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={200}>
              <PieChart>
                <Pie
                  data={getExpensesBreakdown()}
                  cx="50%"
                  cy="50%"
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                  label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                >
                  {getExpensesBreakdown().map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
            <div className="text-2xl font-bold mt-2">₹{calculateTotalSpend().toLocaleString()}</div>
            <div className="text-sm text-gray-500">Expenses breakdown</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Account Balance</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-4xl font-bold">₹{calculateAccountBalance().toLocaleString()}</div>
            <div className="text-green-500 text-lg">
              {((calculateAccountBalance() / calculateTotalRevenue()) * 100).toFixed(2)}% ↑
            </div>
            <div className="text-sm text-gray-500">Account Balance</div>
          </CardContent>
        </Card>
      </div>

      <h2 className="text-2xl font-semibold mb-4">Transactions</h2>
      <div className="flex justify-between items-center mb-4">
        <div className="relative flex-grow mr-4">
          <Search className="absolute left-2 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
          <Input
            className="pl-8"
            placeholder="Search for transaction"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <Button variant="outline">
          <Filter size={20} className="mr-2" /> Filter
        </Button>
      </div>

      <Table>
        <TableHeader>
          <TableRow>
            <TableHead>Type</TableHead>
            <TableHead>Category</TableHead>
            <TableHead>Subcategory</TableHead>
            <TableHead>Description</TableHead>
            <TableHead>Recipient</TableHead>
            <TableHead>Amount</TableHead>
            <TableHead>Date</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {filteredTransactions.map((transaction) => (
            <TableRow key={transaction.id}>
              <TableCell>{transaction.type}</TableCell>
              <TableCell>{transaction.category}</TableCell>
              <TableCell>{transaction.subcategory}</TableCell>
              <TableCell>{transaction.description}</TableCell>
              <TableCell>{transaction.to}</TableCell>
              <TableCell>₹{transaction.amount.toLocaleString()}</TableCell>
              <TableCell>{new Date(transaction.date).toLocaleDateString()}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  );
};

export default Accounts;
